mapper.update:
UPDATE friendship
SET status     = #{status},
    updated_at = NOW()
WHERE user_id = #{userId}
  AND friend_id = #{friendId};

mapper.select:
SELECT u.*
FROM user u
         JOIN friendship f ON (u.id = f.friend_id)
WHERE f.user_id = #{userId}
  AND f.status = 'accepted';

SELECT *
FROM friendship
WHERE friend_id = #{userId}
  AND status = 'pending';
